import React  ,{useState} from 'react'
import './css/MessagePannel.css'
import MessageSenderLayout from './MessageSenderLayout'
import {Row ,Col } from 'react-bootstrap'
import MessageReceiveLayout from './MessageReceiveLayout'
import { useDispatch, useSelector } from 'react-redux'
import { updateMessageQueue } from '../../../features/Messages'
import {format } from 'date-fns'
import SendBtn from './SendBtn'
const MessagePannel = ({socket,cur_user }) => {

    const  messaegeQueue = useSelector(state=>state.msgSlice.messageQueue);
    const  dispatch = useDispatch()

    // #message receive lisitener
    socket.on("message", (payload)=>{
        dispatch(updateMessageQueue(payload))
        console.log("I got the payload",payload)
        console.log(messaegeQueue.length);
      })




  return (
    <React.Fragment>
        <div className='pannelBound'>
            <div className='mainContentChat'>
                    <Row>
                        {

                            // messaegeQueue.map(({sender,sendTo , sumited_at ,message})=>{
                            messaegeQueue.foreach((payload)=>{
                                const {sender,sendTo , sumited_at ,message} = JSON.parse( payload);
                              if(cur_user===sendTo){
                                  return (<MessageReceiveLayout content={message} date={sumited_at} sender={sender}  />)

                                }else if(cur_user=== sender){
                                    return <MessageSenderLayout content={message}  date={sumited_at}  />
                                }
                                return (<div>{JSON.stringify(payload)}</div>)


                            })
                        } 
                    </Row>
                </div>
        </div>
        <SendBtn socket={socket} />
    </React.Fragment>
  )
}

export default MessagePannel
