import { createSlice } from "@reduxjs/toolkit"
import Action from "../Pages/ChatRoot/componet/Action"

let MSG_initState ={
    users :[],
    senderQueue :[],
    receiverQueue:[]
}

const MessageSlice =  createSlice({
    name: "msgSlice", 
    initialState:MSG_initState,
    reducers:{

        setSenderQueue :(state,Action)=>{
            state.senderQueue = Action.payload;
        },
        updateSenderQueue:(state,Action)=>{
            state.senderQueue.push(Action.payload);
        },
        updateReceiverQueue :(state,Action)=>{
            state.receiverQueue.push(Action.payload);
        }

    }
})
export const {setSenderQueue ,updateReceiverQueue ,updateSenderQueue} =MessageSlice.actions;

export default MessageSlice.reducer;
