import {io} from 'socket.io-client'
import {useForm} from 'react-hook-form'
import * as Yup from 'yup'
import { yupResolver } from '@hookform/resolvers/yup';
import dotenv from 'dotenv';
const ChatRoom = () => {

  dotenv.config();
  const [LoginName ,setLoginName ]   = useState()
  const socket =  io("http://127.0.0.1:4085/ChatServer");

  console.log(process.env.SOCKET_GET_CHAT_LOG)
  socket.on("core", (...args)=>{
      console.log(args)
      socket.emit("jointRoom","ksksksks");
  })


  socket.on("message", (data)=>{
    console.log(data);
  })


  const sendName= ()=>{

    const name = document.getElementById("Login").value;
    console.log(name)
    socket.emit("Logined", name);


  }

  const sendMessage = ()=>{
    socket.emit("message" , {
      sender: document.getElementById("Login").value,
      sendTo : document.getElementById("Sendto").value,
      message : document.getElementById("Message").value,
      sumited_at: new Date()
    })
  }
  return (
    <div>


      <div>LoginName : <input type="text" id='Login' name='Login'/> <button onClick={sendName}>send</button></div>
      <div>sendTo : <input type="text" id='Sendto'  name='sendTo'/> <button>send</button></div>
      <div>Message : <input type="text" id='Message'  name='Message'/> <button onClick={sendMessage}>send</button></div>
    </div>


  )
}

export default ChatRoom
