import React from 'react'
import { Row, Stack ,Col } from 'react-bootstrap'
import './css/contactITEM.css'
import {format} from 'date-fns'


const ContactITEM = ({icon ,name, lastestMSG ,dateTime}) => {

    const calText = ()=>{


        const date = format(new Date(dateTime) , "dd/MM/yyyy");
        return <div>{date}</div>;
    }


  return (
    <Stack direction='horizontal' className="Contact_itemContainer" gap={3}>
        <div className="icon"></div>
        <div className="details" style={{width:"80%"}}>
                <div className='itemDateTime'>{calText}</div>
                <div className='name'>{name}</div>
                <div className='latestMSG w-8'>{lastestMSG}</div>
        </div>
    </Stack>
  )
}

export default ContactITEM
