import React  ,{useState} from 'react'
import './css/MessagePannel.css'
import MessageSenderLayout from './MessageSenderLayout'
import {Row ,Col } from 'react-bootstrap'
import MessageReceiveLayout from './MessageReceiveLayout'
import { useDispatch, useSelector } from 'react-redux'
import { updateMessageQueue } from '../../../features/Messages'
import {format } from 'date-fns'
import SendBtn from './SendBtn'
const MessagePannel = ({socket,cur_user , msgQueue, setMsgQueueFn }) => {


    // #message receive lisitener
    socket.on("message", (payload)=>{
        setMsgQueueFn([...msgQueue, payload]);

      })




  return (
    <React.Fragment>
        <div className='pannelBound'>
            <div className='mainContentChat'>
                    <Row>
                        {

                            msgQueue.map(({sender,sendTo , sumited_at ,message})=>{
                              if(cur_user===sendTo){
                                  return (<MessageReceiveLayout content={message} date={sumited_at} sender={sender}  />)

                                }else if(cur_user=== sender){
                                    return <MessageSenderLayout content={message}  date={sumited_at}  />
                                }

                            })
                        } 
                    </Row>
                </div>
        </div>
        <SendBtn socket={socket} />
    </React.Fragment>
  )
}

export default MessagePannel
