import React, { useState } from 'react'
import {Button, Form ,FormControl, Stack} from 'react-bootstrap'

import Axios  from 'axios'
import { useDispatch } from 'react-redux';
import {format} from 'date-fns'
import { date } from 'yup';
import moment, {Moment} from 'moment'
import MessageSenderLayout from './MessageSenderLayout';
import {updateMessageQueue} from '../../../features/Messages'
import {useSelector} from 'react-redux'
import { useMutation, useQueryClient } from '@tanstack/react-query';
import axios from 'axios';
const SendBtn = ({socket  ,ref_Bottom , access_Token ,chatQueryKey}) => {
    const [msg ,setMsg] = useState('');

    const dispatch= useDispatch()    
    const  senderInfo =  useSelector(state => state.msgSlice.MessageHeader)

    const client = useQueryClient()

    const  sendMsg = async( data)=>{
        try{
            return await axios.post("http://127.0.0.1:4085/nusrRoute/sendMessage" ,data ,{
                headers: { 
                    "Authorization" :access_Token , 
                }
            })
        }catch(err){
            console.log(err)
        }
    }

    const {mutate ,isSuccess ,isError} = useMutation(async(payload) => await sendMsg(payload) )

    if(isSuccess){
        alert("done ")
    }

    if(isError)
        alert("erro")



    const send = async()=>{
        const sendDate =new Date();

        const payload = {
                        ...senderInfo ,
                        message : msg,
                        sumited_at: sendDate
        }
        mutate(payload)            
        setMsg('')
        ref_Bottom?.current?.scrollIntoView({ behavior: 'smooth' });




    }
    const focusout=(e)=>{
        socket.emit("typing" ,{...senderInfo, isTyping:false})
    }
    const typing=(e)=>{
        console.log("I am typing")
        socket.emit("typing" ,{...senderInfo, isTyping:true})
        setMsg(e.target.value);


    }
    const SENDBTN_CSS = {
                        width: "0",
                        height: "0",
                        borderTop: "10px solid transparent",
                        borderBottom: "10px solid transparent",
                        borderLeft:"10px solid #f2f0f5",                       
        }

  return (
    <Stack direction='horizontal' gap={3} style={{padding:"10px"}}>

        <FormControl
            style={{backgroundColor:"rgba(198, 219, 227, 0.8) "}}
            type="text"
            placeholder='Enter your message here'
            value={msg}
            onChange={(e)=>typing(e)}
            onBlur={(e)=>focusout(e)}
            />
        <Button onClick={send}> 
            <div style={SENDBTN_CSS}></div>
        </Button>

        </Stack>
  )
}

export default SendBtn
