import React  ,{useState} from 'react'
import './css/MessagePannel.css'
import MessageSenderLayout from './MessageSenderLayout'
import {Row ,Col } from 'react-bootstrap'
import MessageReceiveLayout from './MessageReceiveLayout'
import { useDispatch, useSelector } from 'react-redux'
import { updateMessageQueue } from '../../../features/Messages'
import {format } from 'date-fns'
import SendBtn from './SendBtn'
const MessagePannel = ({socket}) => {

    const  cur_usr = useSelector(state => state.user.profile.username);

    const [messageQueue,setMessageQueue] = useState([]);



    // #message receive lisitener
    socket.on("message", ({sender ,sendTo ,message ,sumited_at})=>{

        // #reformate date
        const sendDate =  format(new Date(sumited_at) , "dd-mm-yyyy") 
        setMessageQueue([...messageQueue , 
            <MessageReceiveLayout content={message} date={sendDate} sender={sender} key={messageQueue.length}/>
        ])
        console.log(message)               
      })




  return (
    <React.Fragment>
        <div className='pannelBound'>
            <div className='mainContentChat'>
                    <Row>
                        {   messageQueue.map(msg=>msg)} 
                    </Row>
                </div>
        </div>
        <SendBtn socket={socket} setQueueFn={setMessageQueue}  QueueMsg={messageQueue}/>
    </React.Fragment>
  )
}

export default MessagePannel
