import React, { useState } from 'react'
import ContactITEM from './ContactITEM'
import './css/shortList.css'
import {getContactList} from'../../../api/getData'
import { useQuery} from '@tanstack/react-query'
import { useSelector } from 'react-redux'
import axios from 'axios'
const ShortList = () => {
  const profile = useSelector(state=>state.user.profile);

  const [contactList ,setContactList] = useState([]);
  const {data ,isLoading ,isFetched,error} =useQuery(["getContactList"] , ()=>{
      return axios.get( "http://127.0.0.1:4085/nusrRoute/getPrivateMessage",{
        headers :{
            Authorization :profile.access_Token
        }
    })
  })

  if(isFetched){

    const result = data.data.map(({participant, sources})=>{
      console.log(sources)
      return {
          sender: participant.filter( usr => usr !== profile.username),
          latestMsg:  sources.slice(-1)
      }
    })
    console.log(result)
  }

    //  const sender = data.map( ({participant,sources})=>{
    //     return participant.filter( (usr) => usr !== profile.username)

    //   })
    //   console.log(sender)

  return (

    <div className='contactList'>
      <ContactITEM
        lastestMSG="Hkskdowwowlsaksaksaksakaksnadjjdjdkakdakdajk"
        name="Garylaw@gmail.com"
        dateTime={"2023-08-26T06:46:36.435Z"}
        con
      />

    </div>
  )
}

export default ShortList
