import React, { useState } from 'react'
import {io} from 'socket.io-client'
import {useForm} from 'react-hook-form'
import * as Yup from 'yup'
import { yupResolver } from '@hookform/resolvers/yup';
import { Container, Stack ,Row,Col} from 'react-bootstrap';
import Action from '../ChatRoot/componet/Action';
import Search from '../ChatRoot/componet/Search';
import ChatRecord from './componet/ShortList';
import ShortList from './componet/ShortList';
import MessageControl from './componet/MessageControl';
import MessageTo from './componet/MessageTo';
import MessagePannel from './componet/MessagePannel';
const ChatRoom = () => {

  const [LoginName ,setLoginName ]   = useState()
  const socket =  io("http://127.0.0.1:4085/ChatServer");

  socket.on("core", (...args)=>{
      console.log(args)
      socket.emit("jointRoom","ksksksks");
  })


  socket.on("message", (data)=>{
    console.log(data);
  })

  socket.on("ChatLog" ,data=>{
    console.log("GetChating Log......")
    console.log(data)
  })

  const sendName= ()=>{

    const name = document.getElementById("Login").value;
    console.log(name)
    socket.emit("Logined", name);


  }

  const sendMessage = ()=>{
    socket.emit("message" , {
      sender: document.getElementById("Login").value,
      sendTo : document.getElementById("Sendto").value,
      message : document.getElementById("Message").value,
      sumited_at: new Date()
    })
  }

  return (
    <React.Fragment>

    <Container fluid={true}  >
      <Row  style={{border:"1px solid red" ,height:"100vh"}}>
        <Col style={{border :"1px solid red"}} xs={3}> 
          <Stack>
            <Action />
            <Search />
            <ShortList/>
          </Stack>
        </Col>
        <Col style={{padding:'0'}}>
          <Stack>
              {/* <MessageTo/> */}
              <MessagePannel/>
              <MessageControl/>
          </Stack>
        </Col>
      </Row>




    </Container>

      <Container hidden={true}>      
        <div>
          <div>LoginName : <input type="text" id='Login' name='Login'/> <button onClick={sendName}>send</button></div>
          <div>sendTo : <input type="text" id='Sendto'  name='sendTo'/> <button>send</button></div>
          <div>Message : <input type="text" id='Message'  name='Message'/> <button onClick={sendMessage}>send</button></div>
        </div>
      </Container>
    </React.Fragment>


  )
}

export default ChatRoom
