import React, { useState } from 'react'
import {Button, Form ,Stack} from 'react-bootstrap'
import { useDispatch } from 'react-redux'
import {createNewContectFn} from '../../../features/Messages'
import { useMutation, useQuery } from '@tanstack/react-query'
import axios from 'axios'

const ContactCreateForm = () => {
    const [contactUsr ,setContactUsr] = useState("")

    const dispatch = useDispatch();


    const createNewContact = async(contactUser)=>{
      const res = await axios.post(`http://127.0.0.1:4085/nusrRoute/createContactUsr?contactUser=${contactUsr}`)   
      (res?.isExist)?  "No such user . Please inpute correct one"  : "User is created";


    }

    const {mutate}= useMutation( async (contactUser)=>await createNewContact(contactUser),{
        onSuccess:(data)=>{
            alert(data)
        },
        enabled  :contactUsr !==""
    })

   const call_API = ()=>{
        mutate(contactUsr)
   }


  return (
    <Form style={{padding:'20px'}}>
        <Form.Group className='mb-4'>
            <Form.Label>Contact users</Form.Label>
             <Form.Control                 
                type="text" 
                className='mt-2'
                value ={contactUsr}
                onChange={(e)=>setContactUsr(e.target.value)}
                placeholder='Enter the username'
             />
        </Form.Group>
        <Stack gap={4}  style={{margin:"auto"}}>
            <Button onClick={call_API}>Submit</Button>
            <Button onClick={()=>dispatch(createNewContectFn(false))}>Return to Contact List </Button>
        </Stack>

    </Form>


  )
}

export default ContactCreateForm
