import './css/MessagePannel.css'
import MessageSenderLayout from './MessageSenderLayout'
import {Row ,Col } from 'react-bootstrap'
import MessageReceiveLayout from './MessageReceiveLayout'
import { useSelector } from 'react-redux'
const MessagePannel = ({socket}) => {

    const [ receiveQueue,setReceiveQueue] = useState([])
    const  cur_usr = useSelector(state => state.user.profile.username);
    socket.on("message", ({sender ,sendTo ,message ,sumited_at})=>{

        console.log(`current usr is ${cur_usr}`)
        if (cur_usr===sendTo){

            setReceiveQueue(
                [
                    ...receiveQueue ,  
                    <MessageReceiveLayout 
                        sender={sender}
                        content={message}
                        date={sumited_at}
                    />            
            ])

        }



      })


  return (
    <div className='pannelBound'>
        <div className='mainContentChat'>
                <Row>
                    <Col className='recevierQueue' xs={13}>
                       {
                       }
                    </Col>
                    <Col className='senderQueue'>
                        <MessageSenderLayout
                            content="Ten years ago, the hero Max defeated the Demon Lord. In order to regain his power, the Demon Lord went into a deep slumber...but when he awakens, his body is only a chibi version of its fearsome form. Now curious to see what his enemy is up to, the Demon Lord visits Max, only to discover the mighty hero living in a dirty one-room apartment. The Demon Lord decides to move in with Max and help his old enemy become a formidable opponent once more!
                            (Source: Seven Seas Entertainment)a"
                        />
