import React, { useState } from 'react'
import './css/MessageTo.css'
import { Row, Col ,Stack} from 'react-bootstrap'
import { useSelector } from 'react-redux'
import { updateSenderStatus } from '../../../features/Messages'
const MessageTo = ({socket}) => {

  const {sendTo} = useSelector(state=>state.msgSlice.MessageHeader)
  const [status ,setStatus] = useState("offine");

  socket.emit("onlineChecking",sendTo);
  socket.on("typing", (res)=>{
    console.log(res)
    setStatus("typing...")
  })

  socket.on("onlineChecking" , (isOnline)=>{
    console.log(isOnline)
    isOnline && setStatus("online")
  })


  return (
    <div className='sendToBound'>
        <Row>
            <Col xs={1}>
                <div className='imageBox'></div>
            </Col>
            <Col>
            <Stack  direction="vertical" gap={1}>
                <div>{sendTo}</div>
                <div className='typing'>{status}</div>

            </Stack>
            </Col>
        </Row>

    </div>
  )
}

export default MessageTo
