import {createSlice ,nanoid} from '@reduxjs/toolkit'

const initState =  {
    profiles :[]
}
const  profileSlice = createSlice({
    name :"userProfile",
    initState,
    reducers:{
        createProfile: (state ,Action)=>{
            const newProfile = {id:nanoid() , text:Action.payload}
            state = {};

        },
        grepProfile :(state,Action)=>{
            return state.content.slice(-1)
        }

    }
})
export const {createProfile,grepProfile} =profileSlice.actions;

export default profileSlice.reducer;
