import React  ,{useState} from 'react'
import './css/MessagePannel.css'
import MessageSenderLayout from './MessageSenderLayout'
import {Row ,Col } from 'react-bootstrap'
import MessageReceiveLayout from './MessageReceiveLayout'
import { useDispatch, useSelector } from 'react-redux'
import { updateMessageQueue } from '../../../features/Messages'
import {format } from 'date-fns'
import SendBtn from './SendBtn'
const MessagePannel = ({socket,cur_user }) => {

    const  messageQueue = useSelector(state=>state.msgSlice.messageQueue);
    const  dispatch = useDispatch()



    // #message receive lisitener
    socket.on("message", (payload)=>{
        console.log("I habe receive the message",messageQueue.length)
        dispatch(updateMessageQueue(payload));
        // #reformate date
        // const sendDate =  format(new Date(sumited_at) , process.env.REACT_APP_DATE_FORMAT) 
        // setMessageQueueFn([...messageQueue , 
        //     <MessageReceiveLayout content={message} date={sendDate} sender={sender}  key={messageQueue.length}/>
        // ])
      })




  return (
    <React.Fragment>
        <div className='pannelBound'>
            <div className='mainContentChat'>
                    <Row>
                        {

                            messageQueue.map(({sender,sendTo , sumited_at ,message})=>{

                              if(cur_user===sender){
                                  return (<MessageReceiveLayout content={message} date={sumited_at} sender={sender}  key={messageQueue.length}/>)

                                }else if(cur_user=== sendTo){
                                    return <MessageSenderLayout content={message}  date={sumited_at} key={messageQueue.length} />
                                }



                            })
                        } 
                    </Row>
                </div>
        </div>
        <SendBtn socket={socket} />
    </React.Fragment>
  )
}

export default MessagePannel
