import React, { useEffect, useState } from 'react'
import {login_Api} from '../../api/getData'
import {useQuery , useMutation , useQueryClient} from '@tanstack/react-query'
import {Row, Col , Form , FormGroup , FormControl ,FormLabel, Container, Button} from 'react-bootstrap'
import {useForm} from 'react-hook-form'
import { yupResolver } from '@hookform/resolvers/yup' 
import * as Yup from 'yup'

const LoginPages = () => {
  const queryClient = useQueryClient();

  const  formSchema = Yup.object().shape({
    email: Yup.string()
    .required("Email address is required !"),

    password: Yup.string().required()

  })
  const {register , handleSubmit  ,formState:{errors}} = useForm({resolver: yupResolver(formSchema)})

  const {data,isLoading}=  useQuery(["sal"], login_Api({    "email": "garylaw696969@gmail.com",
  "password":"kssaajsa22"}))



  const submit =(formData)=>{
    alert("1229")
  }




  if(isLoading) 
    return <h1>The data is Loading ......</h1>                                     

  return (
    <Container>

        <Form>
          <FormGroup>
            <FormLabel>Username</FormLabel>
            <FormControl 
              type='text'
              {...register("username")}
            />
          </FormGroup>
          <FormGroup>
            <FormLabel>Password</FormLabel>
            <FormControl 
              type='password'
              {...register("password")}
            />
          </FormGroup>
          <Button onClick={handleSubmit(submit)}>Submit</Button>
        </Form>

    </Container>
  )
}

export default LoginPages
